mapper.select:
SELECT ml3_agent_role.agent_id as agentId, ml3_agent_role.role_id as roleId FROM tb_ml3_agent_role ml3_agent_role where 1=1
and ml3_agent_role.agent_id=#{agentId}
and ml3_agent_role.role_id=#{roleId}

mapper.insert:
INSERT INTO tb_ml3_agent_role
(
agent_id,
role_id
)
VALUES(
#{agentId},
#{roleId}
)
