mapper.select:
SELECT ml3_permission.id as id, ml3_permission.parent_id as parentId, ml3_permission.parent_ids as parentIds, ml3_permission.name as name, ml3_permission.sort as sort, ml3_permission.href as href, ml3_permission.icon as icon, ml3_permission.is_show as isShow, ml3_permission.permission as permission, ml3_permission.create_date as createDate, ml3_permission.update_date as updateDate, ml3_permission.remarks as remarks, ml3_permission.alias_name as aliasName FROM tb_ml3_permission ml3_permission where ml3_permission.del_flag=0
and ml3_permission.parent_id=#{parentId}
order by ml3_permission.sort

mapper.update:
UPDATE tb_ml3_permission
id = #{id}, name = #{name}, sort = #{sort}, href = #{href}, icon = #{icon}, is_show = #{isShow}, permission = #{permission}, update_by = #{updateBy.id}, update_date = #{updateDate}, alias_name = #{aliasName}
WHERE id= #{id} and del_flag = 0

    UPDATE
tb_ml3_permission set del_flag = 1
    WHERE id= #{id} OR parent_id = #{id}

